{
    PORTB = 0x02; // Activate pull-up

    DDRC = 0xFF; // Output mode for PORTC
    DDRD = 0x0FF; // Output mode for PORTD

    TCCR1 = 0x0006; // External clock source, no prescaling

    TCNT1 = 0x0000; // Set count to 0

    while(1) // Run forever
    {
